sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // 	The basic idea is to check if there is a record already existing with the same mapping.

    var abort = false;
    var message;
    var rec;
	var Table = current.getTableName();

    rec = new GlideRecord(current.getTableName());
    rec.addQuery('u_type_movement', current.u_type_movement);
    rec.addQuery("install_status", current.install_status);
    rec.addQuery("substatus", current.substatus);
    // rec.addQuery("u_origin_movement", current.u_origin_movement);
    rec.addQuery('sys_id', "!=", current.sys_id);
    rec.query();
    //abort = rec.hasNext();
	if (rec.next()) {
	    message = gs.getMessage("Either this mapping already exists with number: " + rec.number);
	    gs.addErrorMessage(message);
	    current.setAbortAction(true);
	}

    // if (abort) {
    //     message = gs.getMessage("Either this mapping already exists with number: " + rec.number);
    //     gs.addErrorMessage(message);
    //     current.setAbortAction(true);
    // }


})(current, previous);
